import{_ as a,o as n,c as r,z as e,a as t}from"./chunks/framework.9482e208.js";const F=JSON.parse('{"title":"§ 45.2-1207  Special Reclamation Fund.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/45.2-1207.md","filePath":"vacode/45.2-1207.md"}'),o={name:"vacode/45.2-1207.md"},s=e("h1",{id:"§-45-2-1207-special-reclamation-fund",tabindex:"-1"},[t("§ 45.2-1207 Special Reclamation Fund. "),e("a",{class:"header-anchor",href:"#§-45-2-1207-special-reclamation-fund","aria-label":'Permalink to "§ 45.2-1207  Special Reclamation Fund."'},"​")],-1),i=e("p",null,'There is hereby created in the state treasury a special nonreverting fund to be known as the Special Reclamation Fund, referred to in this section as "the Fund." The Fund shall be established on the books of the Comptroller. All forfeited bonds collected pursuant to this chapter shall be paid into the state treasury and credited to the Fund. Interest earned on moneys in the Fund shall remain in the Fund and be credited to it. Any moneys remaining in the Fund, including interest thereon, at the end of each fiscal year shall not revert to the general fund but shall remain in the Fund. Moneys in the Fund shall be used solely for the purpose of performing reclamation pursuant to the provisions of this chapter. Expenditures and disbursements from the Fund shall be made by the State Treasurer on warrants issued by the Comptroller upon written request signed by the Director.',-1),l=e("p",null,[t("2021, Sp. Sess. I, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0387",target:"_blank",rel:"noreferrer"},"387"),t(".")],-1),d=[s,i,l];function c(h,u,p,m,f,_){return n(),r("div",null,d)}const g=a(o,[["render",c]]);export{F as __pageData,g as default};
