import{_ as o,o as a,c as r,z as e,a as t}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 45.2-1208  Bond of operator.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/45.2-1208.md","filePath":"vacode/45.2-1208.md"}'),n={name:"vacode/45.2-1208.md"},i=e("h1",{id:"§-45-2-1208-bond-of-operator",tabindex:"-1"},[t("§ 45.2-1208 Bond of operator. "),e("a",{class:"header-anchor",href:"#§-45-2-1208-bond-of-operator","aria-label":'Permalink to "§ 45.2-1208  Bond of operator."'},"​")],-1),s=e("p",null,"Each operator at the time of filing his application shall furnish bond on a form that is prescribed by the Director. Such bond shall be payable to the Department and conditioned on the faithful performance by the operator of all requirements of this chapter and the operations plan as approved and directed by the Department. The amount of bond shall be $3,000 per acre, based upon the number of acres of land that the operator estimates will be affected by mining operations during the next year. Such bond shall be executed by the operator and by a corporate surety licensed to do business in the Commonwealth. However, in lieu of such bond the operator may deposit cash or collateral security acceptable to the Director.",-1),c=e("p",null,[t("1968, c. 734, § 45.1-183; 1970, c. 245; 1972, c. 206; 1974, c. 312; 1977, c. 312; 2017, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0004",target:"_blank",rel:"noreferrer"},"4"),t("; 2021, Sp. Sess. I, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0387",target:"_blank",rel:"noreferrer"},"387"),t(".")],-1),l=[i,s,c];function d(h,p,f,b,u,m){return a(),r("div",null,l)}const v=o(n,[["render",d]]);export{g as __pageData,v as default};
