import{_ as e,o as t,c as n,O as o}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 45.2-1217  Recordation and indexing of lien; notice.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/45.2-1217.md","filePath":"vacode/45.2-1217.md"}'),i={name:"vacode/45.2-1217.md"},a=o('<h1 id="§-45-2-1217-recordation-and-indexing-of-lien-notice" tabindex="-1">§ 45.2-1217 Recordation and indexing of lien; notice. <a class="header-anchor" href="#§-45-2-1217-recordation-and-indexing-of-lien-notice" aria-label="Permalink to &quot;§ 45.2-1217  Recordation and indexing of lien; notice.&quot;">​</a></h1><p>It is the duty of the clerk in whose office the statement described in § <a href="/vacode/45.2-1216/">45.2-1216</a> is filed to record the statement in the deed books of such office, and index the statement in the general index of deeds, in the name of the Commonwealth as well as the owner of the property, showing the type of the lien. From the time of such recording and indexing, all persons shall be deemed to have notice thereof.</p><p>2017, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0004" target="_blank" rel="noreferrer">4</a>, § 45.1-186.5; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0387" target="_blank" rel="noreferrer">387</a>.</p>',3),r=[a];function d(c,s,l,h,f,_){return t(),n("div",null,r)}const m=e(i,[["render",d]]);export{g as __pageData,m as default};
