import{_ as r,o,c as a,z as e,a as t}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 45.2-1230  Agreements with owners or lessees; reclamation by Director.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/45.2-1230.md","filePath":"vacode/45.2-1230.md"}'),n={name:"vacode/45.2-1230.md"},s=e("h1",{id:"§-45-2-1230-agreements-with-owners-or-lessees-reclamation-by-director",tabindex:"-1"},[t("§ 45.2-1230 Agreements with owners or lessees; reclamation by Director. "),e("a",{class:"header-anchor",href:"#§-45-2-1230-agreements-with-owners-or-lessees-reclamation-by-director","aria-label":'Permalink to "§ 45.2-1230  Agreements with owners or lessees; reclamation by Director."'},"​")],-1),i=e("p",null,"The Director is authorized to enter into agreements with owners or lessees of orphaned land when the owners agree to the reclamation of such land by the Division to the extent and in the manner deemed appropriate or reasonable by the Director. The Director shall not return orphaned land to any use other than the minimum potential use of the land that existed prior to the initiation of mining operations unless the landowner or owners, or lessee or lessees, agree to bind themselves to the payment of the additional cost upon terms that the Director deems reasonable. In entering into such agreements, the Director shall be guided by the priorities for reclamation established by him and shall not enter into any such agreement unless funds are immediately available for the performance of the agreement by the Director as provided in this article.",-1),l=e("p",null,[t("1978, c. 634, § 45.1-197.5; 2021, Sp. Sess. I, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0387",target:"_blank",rel:"noreferrer"},"387"),t(".")],-1),h=[s,i,l];function c(d,m,p,f,_,u){return o(),a("div",null,h)}const w=r(n,[["render",c]]);export{g as __pageData,w as default};
