import{_ as e,o as t,c as a,O as n}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 45.2-1234  Minerals Reclamation Fund.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/45.2-1234.md","filePath":"vacode/45.2-1234.md"}'),r={name:"vacode/45.2-1234.md"},o=n('<h1 id="§-45-2-1234-minerals-reclamation-fund" tabindex="-1">§ 45.2-1234 Minerals Reclamation Fund. <a class="header-anchor" href="#§-45-2-1234-minerals-reclamation-fund" aria-label="Permalink to &quot;§ 45.2-1234  Minerals Reclamation Fund.&quot;">​</a></h1><p>There is hereby created in the state treasury a special nonreverting fund to be known as the Minerals Reclamation Fund. The Fund shall be established on the books of the Comptroller. All payments made by operators in accordance with the provisions of this article shall be paid into the state treasury and credited to the Fund. Any moneys remaining in the Fund, including interest thereon, at the end of each fiscal year shall not revert to the general fund but shall remain in the Fund. Moneys in the Fund shall be used solely for the reclamation of mining operations pursuant to § <a href="/vacode/45.2-1238/">45.2-1238</a>. Expenditures and disbursements from the Fund shall be made by the State Treasurer on warrants issued by the Comptroller upon written request signed by the Director.</p><p>1978, c. 634, § 45.1-197.8; 2017, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0004" target="_blank" rel="noreferrer">4</a>; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0387" target="_blank" rel="noreferrer">387</a>.</p>',3),i=[o];function s(l,d,c,h,u,p){return t(),a("div",null,i)}const f=e(r,[["render",s]]);export{m as __pageData,f as default};
