import{_ as e,o as a,c as r,O as n}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 45.2-1404  Standards for regulations.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/45.2-1404.md","filePath":"vacode/45.2-1404.md"}'),t={name:"vacode/45.2-1404.md"},i=n('<h1 id="§-45-2-1404-standards-for-regulations" tabindex="-1">§ 45.2-1404 Standards for regulations. <a class="header-anchor" href="#§-45-2-1404-standards-for-regulations" aria-label="Permalink to &quot;§ 45.2-1404  Standards for regulations.&quot;">​</a></h1><p>In adopting regulations pursuant to § <a href="/vacode/45.2-1401/">45.2-1401</a> or <a href="/vacode/45.2-1402/">45.2-1402</a>, the Director shall consider:</p><ol><li><p>Standards utilized and generally recognized by the underground mineral mining industry;</p></li><li><p>Standards established by recognized professional mineral mining organizations and groups;</p></li><li><p>The federal mine safety law;</p></li><li><p>Research, demonstrations, experiments, and any other information available regarding the maintenance of a reasonable degree of safety protection, including the latest available scientific data in the field, the technical and economic feasibility of such standards, and experience gained under the Act and other mine safety laws; and</p></li><li><p>Any other criteria necessary to ensure the safety and health of miners and other persons or property likely to be affected by any underground mineral mine or related operation.</p></li></ol><p>1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0028" target="_blank" rel="noreferrer">28</a>, § 45.1-161.295; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0387" target="_blank" rel="noreferrer">387</a>.</p>',4),o=[i];function s(d,l,c,p,f,h){return a(),r("div",null,o)}const u=e(t,[["render",s]]);export{_ as __pageData,u as default};
