import{_ as e,o as t,c as a,O as r}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 45.2-1505  Health regulations.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/45.2-1505.md","filePath":"vacode/45.2-1505.md"}'),o={name:"vacode/45.2-1505.md"},i=r('<h1 id="§-45-2-1505-health-regulations" tabindex="-1">§ 45.2-1505 Health regulations. <a class="header-anchor" href="#§-45-2-1505-health-regulations" aria-label="Permalink to &quot;§ 45.2-1505  Health regulations.&quot;">​</a></h1><p>A. The Director may adopt regulations requiring that sources of dust at surface mineral mines be wetted down unless controlled by dry collection measures or other means approved by the Director.</p><p>B. The Director may adopt regulations providing that no miner at a surface mineral mine that is subject to inspection by the Department pursuant to § <a href="/vacode/45.2-1148/">45.2-1148</a> shall be exposed to noise levels that exceed the federal limit adopted by MSHA for non-coal miners. Such regulations shall provide that if such exposure exceeds the federal limit, the Director may require the operator to employ feasible engineering and administrative control measures.</p><p>1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0928" target="_blank" rel="noreferrer">928</a>, § 45.1-161.309; 1997, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0390" target="_blank" rel="noreferrer">390</a>; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0387" target="_blank" rel="noreferrer">387</a>.</p>',4),n=[i];function s(l,c,h,d,p,u){return t(),a("div",null,n)}const f=e(o,[["render",s]]);export{g as __pageData,f as default};
