import{_ as e,o as i,c as o,O as t}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 45.2-1614  Additional duties and responsibilities of the Board.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/45.2-1614.md","filePath":"vacode/45.2-1614.md"}'),a={name:"vacode/45.2-1614.md"},r=t('<h1 id="§-45-2-1614-additional-duties-and-responsibilities-of-the-board" tabindex="-1">§ 45.2-1614 Additional duties and responsibilities of the Board. <a class="header-anchor" href="#§-45-2-1614-additional-duties-and-responsibilities-of-the-board" aria-label="Permalink to &quot;§ 45.2-1614  Additional duties and responsibilities of the Board.&quot;">​</a></h1><p>A. In executing its duties under this chapter, the Board shall:</p><ol><li><p>Foster, encourage, and promote the safe and efficient exploration for and development, production, and conservation of gas and oil resources located in the Commonwealth;</p></li><li><p>Administer a method of gas and oil conservation for the purpose of maximizing exploration, development, production, and utilization of gas and oil resources;</p></li><li><p>Administer procedures for the recognition and protection of the rights of gas or oil owners with interests in gas or oil resources contained within a pool;</p></li><li><p>Promote the maximum production and recovery of coal without substantially affecting the right of a gas owner proposing a gas well to explore for and produce gas; and</p></li><li><p>Hear and decide appeals of the Director&#39;s decisions and orders issued under Article 3 (§ <a href="/vacode/45.2-1629/">45.2-1629</a> et seq.).</p></li></ol><p>B. Without limiting its general authority, the Board has the specific authority to issue regulations or orders pursuant to the provisions of the Administrative Process Act (§ <a href="/vacode/2.2-4000/">2.2-4000</a> et seq.) in order to:</p><ol><li><p>Prevent waste through the design, spacing, or unitization of wells, pools, or fields.</p></li><li><p>Protect correlative rights.</p></li><li><p>Enter spacing and pooling orders.</p></li><li><p>Establish drilling units.</p></li><li><p>Establish maximum allowable production rates for the prevention of waste and the protection of correlative rights.</p></li><li><p>Provide for the maximum recovery of coal.</p></li><li><p>Classify pools and wells as gas, oil, gas and oil, or coalbed methane gas.</p></li><li><p>Collect data, make investigations and inspections, examine property, leases, papers, books, and records, and require or provide for the keeping of records and the making of reports.</p></li><li><p>Set application fees.</p></li><li><p>Govern practices and procedures before the Board.</p></li><li><p>Require additional data from parties to any hearing.</p></li><li><p>Take actions reasonably necessary to carry out the provisions of this chapter.</p></li></ol><p>1982, c. 347, § 45.1-296; 1987, c. 452; 1990, c. 92, § 45.1-361.15; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0387" target="_blank" rel="noreferrer">387</a>.</p>',6),s=[r];function n(l,d,p,c,h,f){return i(),o("div",null,s)}const m=e(a,[["render",n]]);export{g as __pageData,m as default};
