import{_ as e,o as a,c as o,O as t}from"./chunks/framework.9482e208.js";const b=JSON.parse('{"title":"§ 45.2-1616  Statewide spacing of wells.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/45.2-1616.md","filePath":"vacode/45.2-1616.md"}'),l={name:"vacode/45.2-1616.md"},r=t('<h1 id="§-45-2-1616-statewide-spacing-of-wells" tabindex="-1">§ 45.2-1616 Statewide spacing of wells. <a class="header-anchor" href="#§-45-2-1616-statewide-spacing-of-wells" aria-label="Permalink to &quot;§ 45.2-1616  Statewide spacing of wells.&quot;">​</a></h1><p>A. Unless prior approval has been received from the Board or a provision of the field or pool rules so allows:</p><ol><li><p>No well drilled in search of oil shall be located closer than 1,250 feet to any well completed in the same pool; however, this spacing requirement is subject to § <a href="/vacode/45.2-1612/">45.2-1612</a>;</p></li><li><p>No well drilled in search of gas shall be located closer than 2,500 feet to any other well completed in the same pool or closer than 2,500 feet to any storage well within the boundary of a gas storage field certificated by the State Corporation Commission prior to January 1, 1997, if the well to be drilled is to be completed within the same horizon as the certificated gas storage field; and</p></li><li><p>No well shall be drilled closer to the boundary of the acreage supporting the well, whether such acreage is a single leasehold or other tract or a contractual or statutory drilling unit, than one-half of the applicable minimum well spacing distance prescribed in this section.</p></li></ol><p>B. Unless prior approval has been received from the Board or a provision of the field or pool rules so allows:</p><ol><li><p>No well drilled in search of coalbed methane gas shall be located closer than 1,000 feet to any other coalbed methane gas well, or in the case of a coalbed methane gas well located in the gob, closer than 500 feet to any other coalbed methane gas well located in the gob.</p></li><li><p>No coalbed methane gas well shall be drilled closer than 500 feet, or in the case of a well located in the gob, closer than 250 feet, from the boundary of the acreage supporting the well, whether such acreage is a single leasehold or other tract or a contractual or statutory drilling unit.</p></li><li><p>The spacing limitations set forth in this subsection are subject to the provisions of §§ <a href="/vacode/45.2-1611/">45.2-1611</a> and <a href="/vacode/45.2-1612/">45.2-1612</a>.</p></li></ol><p>1990, c. 92, § 45.1-361.17; 1996, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0854" target="_blank" rel="noreferrer">854</a>; 1997, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0759" target="_blank" rel="noreferrer">759</a>; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0387" target="_blank" rel="noreferrer">387</a>.</p>',6),i=[r];function s(n,c,h,d,p,f){return a(),o("div",null,i)}const w=e(l,[["render",s]]);export{b as __pageData,w as default};
