import{_ as e,o,c as r,O as t}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 45.2-1630  Powers, duties, and responsibilities of the Inspector.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/45.2-1630.md","filePath":"vacode/45.2-1630.md"}'),a={name:"vacode/45.2-1630.md"},i=t('<h1 id="§-45-2-1630-powers-duties-and-responsibilities-of-the-inspector" tabindex="-1">§ 45.2-1630 Powers, duties, and responsibilities of the Inspector. <a class="header-anchor" href="#§-45-2-1630-powers-duties-and-responsibilities-of-the-inspector" aria-label="Permalink to &quot;§ 45.2-1630  Powers, duties, and responsibilities of the Inspector.&quot;">​</a></h1><p>A. The Inspector shall administer the laws and regulations and shall have access to all records and properties necessary for this purpose. He shall perform all duties delegated by the Director pursuant to § <a href="/vacode/45.2-105/">45.2-105</a> and maintain permanent records of the following:</p><ol><li><p>Each application for a gas, oil, or geophysical operation and each permitted gas, oil, or geophysical operation;</p></li><li><p>Meetings, actions, and orders of the Board;</p></li><li><p>Each petition for mining coal within 200 feet of or through a well;</p></li><li><p>Each request for special plugging by a coal owner or coal operator; and</p></li><li><p>All other records prepared pursuant to this chapter.</p></li></ol><p>B. The Inspector or another Department employee as determined by the Director shall serve as the principal executive of the staff of the Board.</p><p>C. The Inspector may take charge of well or corehole operations or pipeline emergency operations whenever a well or corehole blowout, release of hydrogen sulfide or other gas, or other serious accident occurs.</p><p>1982, c. 347, § 45.1-293; 1987, c. 452; 1989, c. 164; 1990, c. 92, § 45.1-361.28; 2021, Sp. Sess. I, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0387" target="_blank" rel="noreferrer">387</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0532" target="_blank" rel="noreferrer">532</a>.</p>',6),s=[i];function n(l,p,c,h,d,f){return o(),r("div",null,s)}const g=e(a,[["render",n]]);export{u as __pageData,g as default};
