import{_ as e,o as t,c as a,O as o}from"./chunks/framework.9482e208.js";const m=JSON.parse(`{"title":"§ 45.2-1638  Appeals of Director's decisions to the Board.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/45.2-1638.md","filePath":"vacode/45.2-1638.md"}`),i={name:"vacode/45.2-1638.md"},r=o('<h1 id="§-45-2-1638-appeals-of-director-s-decisions-to-the-board" tabindex="-1">§ 45.2-1638 Appeals of Director&#39;s decisions to the Board. <a class="header-anchor" href="#§-45-2-1638-appeals-of-director-s-decisions-to-the-board" aria-label="Permalink to &quot;§ 45.2-1638  Appeals of Director&#39;s decisions to the Board.&quot;">​</a></h1><p>A. Any person with standing under the provisions of § <a href="/vacode/45.2-1632/">45.2-1632</a> who is aggrieved by a decision of the Director may appeal to the Board, subject to the limitations imposed by subsection B, by petition to the Board filed within 10 days following the appealed decision.</p><p>B. No petition for appeal may raise any matter other than a matter that was raised by the Director or that the petitioner put in issue either by application or by an objection, proposal, or claim made and specified in writing at the informal fact-finding hearing held under § <a href="/vacode/45.2-1637/">45.2-1637</a> leading to the appealed decision.</p><p>1982, c. 347, § 45.1-325; 1984, c. 590; 1990, c. 92, § 45.1-361.36; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0387" target="_blank" rel="noreferrer">387</a>.</p>',4),s=[r];function n(d,c,p,h,l,_){return t(),a("div",null,s)}const b=e(i,[["render",n]]);export{m as __pageData,b as default};
