import{_ as e,o as a,c as t,O as o}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 45.2-1644  Safety in coalbed methane gas, oil, and geophysical operations.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/45.2-1644.md","filePath":"vacode/45.2-1644.md"}'),n={name:"vacode/45.2-1644.md"},r=o('<h1 id="§-45-2-1644-safety-in-coalbed-methane-gas-oil-and-geophysical-operations" tabindex="-1">§ 45.2-1644 Safety in coalbed methane gas, oil, and geophysical operations. <a class="header-anchor" href="#§-45-2-1644-safety-in-coalbed-methane-gas-oil-and-geophysical-operations" aria-label="Permalink to &quot;§ 45.2-1644  Safety in coalbed methane gas, oil, and geophysical operations.&quot;">​</a></h1><p>The Director shall inspect permitted coalbed methane well and related facility operations to ensure the safety of persons on permitted sites. If an inspection reveals any hazardous condition that creates an imminent danger, the Director shall issue a closure order pursuant to § <a href="/vacode/45.2-1629/">45.2-1629</a> requiring the area to be cleared or the equipment removed from use, except for (i) work necessary to continue to vent methane from an active underground mine if such work can be done safely and (ii) any work necessary to correct or eliminate the imminent danger. The Director shall lift the closure order when he finds that the imminent danger has been corrected or eliminated. If an inspection reveals any other condition that creates a risk to the safety or health of any person on the permitted site, the Director shall notify the Department of Labor and Industry for actions under Title 40.1, as applicable.</p><p>1997, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0421" target="_blank" rel="noreferrer">421</a>, § 45.1-361.42; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0387" target="_blank" rel="noreferrer">387</a>.</p>',3),i=[r];function s(c,l,d,h,p,f){return a(),t("div",null,i)}const g=e(n,[["render",s]]);export{_ as __pageData,g as default};
