import{_ as a,o as e,c as r,O as t}from"./chunks/framework.9482e208.js";const h=JSON.parse('{"title":"§ 45.2-1647  Hydraulic fracturing; groundwater management area.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/45.2-1647.md","filePath":"vacode/45.2-1647.md"}'),n={name:"vacode/45.2-1647.md"},o=t('<h1 id="§-45-2-1647-hydraulic-fracturing-groundwater-management-area" tabindex="-1">§ 45.2-1647 Hydraulic fracturing; groundwater management area. <a class="header-anchor" href="#§-45-2-1647-hydraulic-fracturing-groundwater-management-area" aria-label="Permalink to &quot;§ 45.2-1647  Hydraulic fracturing; groundwater management area.&quot;">​</a></h1><p>No person shall conduct any hydraulic fracturing in any well that has been drilled through any portion of a groundwater management area declared by regulation prior to January 1, 2020, pursuant to the provisions of the Ground Water Management Act of 1992 (§ <a href="/vacode/62.1-254/">62.1-254</a> et seq.). For purposes of this section, &quot;hydraulic fracturing&quot; means the treatment of a well by the application of hydraulic fracturing fluid, including a base fluid and any additive, under pressure for the express purpose of initiating or propagating fractures in a target geologic formation to enhance production of natural gas or oil.</p><p>2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0626" target="_blank" rel="noreferrer">626</a>; § 62.1-195.3; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0387" target="_blank" rel="noreferrer">387</a>.</p>',3),i=[o];function c(u,d,l,s,g,f){return e(),r("div",null,i)}const _=a(n,[["render",c]]);export{h as __pageData,_ as default};
