import{_ as e,o as t,c as a,O as r}from"./chunks/framework.9482e208.js";const g=JSON.parse(`{"title":"§ 45.2-1648  Operator's right to sample water.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/45.2-1648.md","filePath":"vacode/45.2-1648.md"}`),o={name:"vacode/45.2-1648.md"},n=r('<h1 id="§-45-2-1648-operator-s-right-to-sample-water" tabindex="-1">§ 45.2-1648 Operator&#39;s right to sample water. <a class="header-anchor" href="#§-45-2-1648-operator-s-right-to-sample-water" aria-label="Permalink to &quot;§ 45.2-1648  Operator&#39;s right to sample water.&quot;">​</a></h1><p>An operator may enter upon surface land at reasonable times and in a reasonable manner to obtain samples of water from any water well that is (i) located within 1,320 feet of a proposed or existing gas well and (ii) actually being utilized by the surface owner or occupant for domestic use. If the surface owner or occupant refuses to allow the operator to sample or causes the operator to be prevented from sampling any such water well, the operator shall promptly notify the Department of such refusal or prevention. The Department shall maintain a record of such notifications. In the event of such a refusal or prevention, the surface owner shall not be entitled to the remedies set forth in § <a href="/vacode/45.2-1649/">45.2-1649</a>.</p><p>1998, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0227" target="_blank" rel="noreferrer">227</a>, § 45.1-361.43; 2009, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0293" target="_blank" rel="noreferrer">293</a>; 2017, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0017" target="_blank" rel="noreferrer">17</a>; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0387" target="_blank" rel="noreferrer">387</a>.</p>',3),i=[n];function s(l,p,c,h,f,_){return t(),a("div",null,i)}const m=e(o,[["render",s]]);export{g as __pageData,m as default};
