import{_ as e,o as i,c as n,O as t}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 45.2-1701  Division of Renewable Energy and Energy Efficiency established; powers and duties.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/45.2-1701.md","filePath":"vacode/45.2-1701.md"}'),a={name:"vacode/45.2-1701.md"},o=t('<h1 id="§-45-2-1701-division-of-renewable-energy-and-energy-efficiency-established-powers-and-duties" tabindex="-1">§ 45.2-1701 Division of Renewable Energy and Energy Efficiency established; powers and duties. <a class="header-anchor" href="#§-45-2-1701-division-of-renewable-energy-and-energy-efficiency-established-powers-and-duties" aria-label="Permalink to &quot;§ 45.2-1701  Division of Renewable Energy and Energy Efficiency established; powers and duties.&quot;">​</a></h1><p>A. The Division of Renewable Energy and Energy Efficiency is established in the Department. The Director has the immediate authority to coordinate the development and implementation of energy policy in the Commonwealth.</p><p>B. The Division shall coordinate the energy-related activities of the various state agencies and advise the Governor on energy issues that arise at the local, state, and national levels. All state agencies and institutions shall cooperate fully with the Division to assist in the proper execution of the duties assigned by this section.</p><p>C. In addition, the Division is authorized to make and enter into all contracts and agreements necessary or incidental to the performance of its duties or the execution of its powers, including the implementation of energy information and conservation plans and programs.</p><p>D. The Division shall:</p><ol><li><p>Consult with state agencies and institutions concerning energy-related activities or policies as needed for the proper execution of the duties assigned to the Division by this section;</p></li><li><p>Serve as the Commonwealth&#39;s liaison with appropriate agencies of the federal government concerning the activities of the federal government related to energy production, consumption, and transportation and energy resource management in general;</p></li><li><p>Provide services to encourage efforts by and among businesses in the Commonwealth, industries, utilities, academic institutions, state and local governments, and private institutions to develop energy resources and energy conservation programs;</p></li><li><p>In consultation with the State Corporation Commission, the Department of Environmental Quality, and the Virginia Center for Coal and Energy Research, prepare the Virginia Energy Plan pursuant to § <a href="/vacode/45.2-1710/">45.2-1710</a>; and</p></li><li><p>Observe the energy-related activities of state agencies and advise such agencies in order to encourage conformity with established energy policy.</p></li></ol><p>1984, c. 590, § 45.1-390; 2006, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0939" target="_blank" rel="noreferrer">939</a>; 2016, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0518" target="_blank" rel="noreferrer">518</a>; 2021, Sp. Sess. I, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0387" target="_blank" rel="noreferrer">387</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0532" target="_blank" rel="noreferrer">532</a>.</p>',7),r=[o];function s(l,c,d,h,p,g){return i(),n("div",null,r)}const v=e(a,[["render",s]]);export{u as __pageData,v as default};
