import{_ as e,o as a,c as t,O as i}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 45.2-1705  Legislative findings.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/45.2-1705.md","filePath":"vacode/45.2-1705.md"}'),n={name:"vacode/45.2-1705.md"},o=i('<h1 id="§-45-2-1705-legislative-findings" tabindex="-1">§ 45.2-1705 Legislative findings. <a class="header-anchor" href="#§-45-2-1705-legislative-findings" aria-label="Permalink to &quot;§ 45.2-1705  Legislative findings.&quot;">​</a></h1><p>The General Assembly hereby finds that:</p><ol><li><p>Energy is essential to the health, safety, and welfare of the people of the Commonwealth and to the Commonwealth&#39;s economy;</p></li><li><p>The government of the Commonwealth should facilitate the availability and delivery of reliable and adequate supplies of energy to industrial, commercial, and residential users at reasonable costs so that such users and the Commonwealth&#39;s economy are able to be productive;</p></li><li><p>The Commonwealth would benefit from articulating clear objectives pertaining to energy issues, adopting an energy policy that advances such objectives, and establishing a procedure for measuring the implementation of such policy;</p></li><li><p>Climate change is an urgent and pressing challenge for the Commonwealth. Swift decarbonization and a transition to clean energy are required to meet the urgency of the challenge; and</p></li><li><p>The Commonwealth will benefit from being a leader in deploying a low-carbon energy economy.</p></li></ol><p>2006, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0939" target="_blank" rel="noreferrer">939</a>, § 67-100; 2020, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1191" target="_blank" rel="noreferrer">1191</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1192" target="_blank" rel="noreferrer">1192</a>; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0387" target="_blank" rel="noreferrer">387</a>.</p>',4),l=[o];function r(s,c,h,g,d,p){return a(),t("div",null,l)}const _=e(n,[["render",r]]);export{m as __pageData,_ as default};
