import{_ as e,o,c as n,O as i}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 45.2-1708  Role of local governments in achieving objectives of the Commonwealth Clean Energy Policy.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/45.2-1708.md","filePath":"vacode/45.2-1708.md"}'),t={name:"vacode/45.2-1708.md"},a=i('<h1 id="§-45-2-1708-role-of-local-governments-in-achieving-objectives-of-the-commonwealth-clean-energy-policy" tabindex="-1">§ 45.2-1708 Role of local governments in achieving objectives of the Commonwealth Clean Energy Policy. <a class="header-anchor" href="#§-45-2-1708-role-of-local-governments-in-achieving-objectives-of-the-commonwealth-clean-energy-policy" aria-label="Permalink to &quot;§ 45.2-1708  Role of local governments in achieving objectives of the Commonwealth Clean Energy Policy.&quot;">​</a></h1><p>A. In the development of any local ordinance addressing the siting of renewable energy facilities that generate electricity from wind or solar resources, such ordinance shall:</p><ol><li><p>Be consistent with the provisions of the Commonwealth Clean Energy Policy pursuant to subsection E of § <a href="/vacode/45.2-1706.1/">45.2-1706.1</a>;</p></li><li><p>Provide reasonable criteria to be addressed in the siting of any renewable energy facility that generates electricity from wind or solar resources. Such criteria shall provide for the protection of the locality in a manner consistent with the goals of the Commonwealth to promote the generation of energy from wind and solar resources; and</p></li><li><p>Include provisions establishing reasonable requirements upon the siting of any such renewable energy facility, including provisions limiting noise, requiring buffer areas and setbacks, and addressing generation facility decommissioning.</p></li></ol><p>B. Any measures required by an ordinance adopted pursuant to subsection A shall be consistent with the locality&#39;s existing ordinances.</p><p>2011, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0750" target="_blank" rel="noreferrer">750</a>, § 67-103; 2021, Sp. Sess. I, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0327" target="_blank" rel="noreferrer">327</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0387" target="_blank" rel="noreferrer">387</a>.</p>',5),r=[a];function l(s,c,h,g,f,d){return o(),n("div",null,r)}const v=e(t,[["render",l]]);export{m as __pageData,v as default};
