import{_ as e,o as t,c as i,O as n}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 45.2-1712  Annual reporting by investor-owned public utilities.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/45.2-1712.md","filePath":"vacode/45.2-1712.md"}'),o={name:"vacode/45.2-1712.md"},r=n('<h1 id="§-45-2-1712-annual-reporting-by-investor-owned-public-utilities" tabindex="-1">§ 45.2-1712 Annual reporting by investor-owned public utilities. <a class="header-anchor" href="#§-45-2-1712-annual-reporting-by-investor-owned-public-utilities" aria-label="Permalink to &quot;§ 45.2-1712  Annual reporting by investor-owned public utilities.&quot;">​</a></h1><p>Each investor-owned public utility providing electric service in the Commonwealth shall prepare an annual report disclosing its efforts to conserve energy, including (i) its implementation of customer demand-side management programs and (ii) efforts by the utility to improve efficiency and conserve energy in its internal operations pursuant to § <a href="/vacode/56-235.1/">56-235.1</a>. The utility shall submit each annual report to the Division by November 1 of each year, and the Division shall compile the reports of the utilities and submit the compilation to the Governor and the General Assembly as provided in the procedures of the Division of Legislative Automated Systems for the processing of legislative documents.</p><p>2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0651" target="_blank" rel="noreferrer">651</a>, § 67-202.1; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0387" target="_blank" rel="noreferrer">387</a>.</p>',3),a=[r];function s(l,c,p,d,u,h){return t(),i("div",null,a)}const m=e(o,[["render",s]]);export{f as __pageData,m as default};
