import{_ as e,o as i,c as t,O as r}from"./chunks/framework.9482e208.js";const p=JSON.parse('{"title":"§ 45.2-1714  Virginia Coastal Energy Research Consortium established; board of directors.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/45.2-1714.md","filePath":"vacode/45.2-1714.md"}'),o={name:"vacode/45.2-1714.md"},n=r('<h1 id="§-45-2-1714-virginia-coastal-energy-research-consortium-established-board-of-directors" tabindex="-1">§ 45.2-1714 Virginia Coastal Energy Research Consortium established; board of directors. <a class="header-anchor" href="#§-45-2-1714-virginia-coastal-energy-research-consortium-established-board-of-directors" aria-label="Permalink to &quot;§ 45.2-1714  Virginia Coastal Energy Research Consortium established; board of directors.&quot;">​</a></h1><p>A. The Virginia Coastal Energy Research Consortium is hereby established to include Old Dominion University, the Virginia Institute of Marine Science of The College of William and Mary in Virginia, the Advanced Research Institute of Virginia Polytechnic Institute and State University, James Madison University, Norfolk State University, Virginia Commonwealth University, Hampton University, George Mason University, and the University of Virginia and is to be located at Old Dominion University.</p><p>B. The Consortium shall be governed by a board of directors (the Board), which shall consist of 16 voting members as follows: the Director or his designee, the Commissioner of Marine Resources or his designee, the President of the Virginia Manufacturers Association or his appointed member of the maritime manufacturing industry, the President of the Virginia Maritime Association or his appointed member of the maritime industry, the Director of the Advanced Research Institute of Virginia Polytechnic Institute and State University or his designee, the President of Old Dominion University or his designee, the Director of the Virginia Institute of Marine Science of The College of William and Mary in Virginia or his designee, the President of Norfolk State University or his designee, the President of James Madison University or his designee, the President of Virginia Commonwealth University or his designee, the President of the University of Virginia or his designee, the President of Hampton University or his designee, the President of George Mason University or his designee, the chairman of the Hampton Roads Technology Council or his appointed member of the technology community, the Director of the Hampton Roads Clean Cities Coalition or his appointed member of the renewable energy industry, and the Director of the Department of Environmental Quality or his designee as the lead agency for the Virginia Coastal Zone Management Program.</p><p>In addition, a representative of the National Aeronautics and Space Administration&#39;s Langley Research Center, to be selected by the Director of the Research Center, shall serve as a nonvoting ex officio member of the Board.</p><p>2006, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0939" target="_blank" rel="noreferrer">939</a>, §§ 67-600, 67-602; 2007, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0904" target="_blank" rel="noreferrer">904</a>; 2009, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0575" target="_blank" rel="noreferrer">575</a>; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0695" target="_blank" rel="noreferrer">695</a>; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0387" target="_blank" rel="noreferrer">387</a>.</p>',5),a=[n];function s(h,d,l,c,g,f){return i(),t("div",null,a)}const v=e(o,[["render",s]]);export{p as __pageData,v as default};
