import{_ as e,o as t,c as a,O as r}from"./chunks/framework.9482e208.js";const y=JSON.parse('{"title":"§ 45.2-1806  Powers and duties of the Authority.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/45.2-1806.md","filePath":"vacode/45.2-1806.md"}'),i={name:"vacode/45.2-1806.md"},n=r('<h1 id="§-45-2-1806-powers-and-duties-of-the-authority" tabindex="-1">§ 45.2-1806 Powers and duties of the Authority. <a class="header-anchor" href="#§-45-2-1806-powers-and-duties-of-the-authority" aria-label="Permalink to &quot;§ 45.2-1806  Powers and duties of the Authority.&quot;">​</a></h1><p>In addition to the other powers and duties established under this article, the Authority has the power and duty to:</p><ol><li><p>Adopt, use, and alter at will an official seal;</p></li><li><p>Make bylaws for the management and regulation of its affairs;</p></li><li><p>Maintain an office at any place within the Commonwealth it designates;</p></li><li><p>Accept, hold, and administer moneys, grants, securities, or other property transferred, given, or bequeathed to the Authority, absolutely or in trust, from any source, public or private, for the purposes for which the Authority is established;</p></li><li><p>Make and execute contracts and all other instruments and agreements necessary or convenient for the exercise of its powers and functions;</p></li><li><p>Employ, in its discretion, consultants, attorneys, architects, engineers, accountants, financial experts, investment bankers, superintendents, managers, and any other employees and agents necessary, and fix their compensation to be payable from funds made available to the Authority;</p></li><li><p>Invest its funds as permitted by applicable law;</p></li><li><p>Receive and accept from any federal or private agency, foundation, corporation, association, or person grants, donations of money, or real or personal property for the benefit of the Authority, and receive and accept from the Commonwealth or any other state, from any municipality, county, or other political subdivision thereof, or from any other source, aid or contributions of either money, property, or other things of value, to be held, used, and applied for the purposes for which such grants and contributions may be made;</p></li><li><p>Enter into agreements with any department, agency, or instrumentality of the United States or of the Commonwealth and with lenders and enter into loans with contracting parties for the purpose of planning, regulating, and providing for the financing or assisting in the financing of any project;</p></li><li><p>Do any lawful act necessary or appropriate to carry out the powers granted or reasonably implied in this article;</p></li><li><p>Identify and take steps to mitigate existing state and regulatory or administrative barriers to the development of the offshore wind energy industry, including facilitating any permitting processes; and</p></li><li><p>Enter into interstate partnerships to develop the offshore wind energy industry and offshore wind energy projects.</p></li></ol><p>2010, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0507" target="_blank" rel="noreferrer">507</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0681" target="_blank" rel="noreferrer">681</a>, § 67-1207; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0387" target="_blank" rel="noreferrer">387</a>.</p>',4),o=[n];function s(l,p,d,h,c,f){return t(),a("div",null,o)}const g=e(i,[["render",s]]);export{y as __pageData,g as default};
