import{_ as e,o as t,c as o,O as r}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 45.2-1810  Declaration of public purpose; exemption from taxation.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/45.2-1810.md","filePath":"vacode/45.2-1810.md"}'),a={name:"vacode/45.2-1810.md"},i=r('<h1 id="§-45-2-1810-declaration-of-public-purpose-exemption-from-taxation" tabindex="-1">§ 45.2-1810 Declaration of public purpose; exemption from taxation. <a class="header-anchor" href="#§-45-2-1810-declaration-of-public-purpose-exemption-from-taxation" aria-label="Permalink to &quot;§ 45.2-1810  Declaration of public purpose; exemption from taxation.&quot;">​</a></h1><p>A. The exercise of the powers granted by this article shall be in all respects for the benefit of the citizens of the Commonwealth and for the promotion of their welfare, convenience, and prosperity.</p><p>B. The Authority shall be deemed to be performing an essential governmental function in the exercise of the powers conferred upon it by this article, and the property of the Authority and its income and operations shall be exempt from taxation or assessments upon any property acquired or used by the Authority under the provisions of this article.</p><p>2010, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0507" target="_blank" rel="noreferrer">507</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0681" target="_blank" rel="noreferrer">681</a>, § 67-1211; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0387" target="_blank" rel="noreferrer">387</a>.</p>',4),n=[i];function p(s,c,l,f,h,d){return t(),o("div",null,n)}const u=e(a,[["render",p]]);export{m as __pageData,u as default};
