import{_ as e,o as t,c as n,O as i}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 45.2-1906 (Effective October 1, 2021; Expires July 1, 2025) Powers and duties of the Authority.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/45.2-1906.md","filePath":"vacode/45.2-1906.md"}'),a={name:"vacode/45.2-1906.md"},o=i('<h1 id="§-45-2-1906-effective-october-1-2021-expires-july-1-2025-powers-and-duties-of-the-authority" tabindex="-1">§ 45.2-1906 (Effective October 1, 2021; Expires July 1, 2025) Powers and duties of the Authority. <a class="header-anchor" href="#§-45-2-1906-effective-october-1-2021-expires-july-1-2025-powers-and-duties-of-the-authority" aria-label="Permalink to &quot;§ 45.2-1906 (Effective October 1, 2021; Expires July 1, 2025) Powers and duties of the Authority.&quot;">​</a></h1><p>In addition to other powers and duties established under this article, the Authority has the power and duty to:</p><ol><li><p>Adopt, use, and alter at will an official seal;</p></li><li><p>Make bylaws for the management and regulation of its affairs;</p></li><li><p>Maintain an office at any place within the Commonwealth it designates;</p></li><li><p>Accept, hold, and administer moneys, grants, securities, or other property transferred, given, or bequeathed to the Authority, absolutely or in trust, from any source, public or private, for the purposes for which the Authority is established;</p></li><li><p>Make and execute contracts and all other instruments and agreements necessary or convenient for the exercise of its powers and functions;</p></li><li><p>Employ, in its discretion, consultants, attorneys, architects, engineers, accountants, financial experts, investment bankers, superintendents, managers, and any other employees and agents necessary and fix their compensation to be payable from funds made available to the Authority;</p></li><li><p>Invest its funds as permitted by applicable law;</p></li><li><p>Receive and accept from any federal or private agency, foundation, corporation, association, or person grants, donations of money, or real or personal property for the benefit of the Authority, and receive and accept from the Commonwealth or any other state, from any municipality, county, or other political subdivision thereof, or from any other source, aid or contributions of either money, property, or other things of value, to be held, used, and applied for the purposes for which such grants and contributions may be made;</p></li><li><p>Enter into agreements with any department, agency, or instrumentality of the United States or of the Commonwealth and with lenders and enter into loans with contracting parties for the purpose of planning, regulating, and providing for the financing or assisting in the financing of any project;</p></li><li><p>Do any lawful act necessary or appropriate to carry out the powers granted or reasonably implied in this article;</p></li><li><p>Identify and take steps to mitigate existing state and regulatory or administrative barriers to the development of the solar energy and energy storage industries, including facilitating any permitting processes;</p></li><li><p>Enter into interstate partnerships to develop the solar energy industry, solar energy projects, and energy storage projects;</p></li><li><p>Collaborate with entities, including institutions of higher education, to increase the training and development of the workforce needed by the solar and energy storage industries in the Commonwealth, including industry-recognized credentials and certifications;</p></li><li><p>Conduct any other activities as may seem appropriate to increase solar energy generation in the Commonwealth and the associated jobs and economic development and competitiveness benefits, including assisting investor-owned utilities in the planned deployment of at least 400 megawatts of solar energy projects in the Commonwealth by 2020 through entering into agreements in its discretion in any manner provided by law for the purpose of planning and providing for the financing or assisting in the financing of the construction or purchase of such solar energy projects authorized pursuant to § <a href="/vacode/56-585.1/">56-585.1</a>;</p></li><li><p>Promote collaborative efforts among the Commonwealth&#39;s public and private institutions of higher education in research, development, and commercialization efforts related to energy storage;</p></li><li><p>Monitor relevant developments in energy storage technology and deployment nationally and globally and disseminate relevant information and research results; and</p></li><li><p>Identify and work with the Commonwealth&#39;s industries and nonprofit partners in advancing efforts related to the development and commercialization of energy storage.</p></li></ol><p>2015, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0090" target="_blank" rel="noreferrer">90</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0398" target="_blank" rel="noreferrer">398</a>, § 67-1505; 2017, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0813" target="_blank" rel="noreferrer">813</a>; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0387" target="_blank" rel="noreferrer">387</a>.</p>',4),r=[o];function s(l,p,d,c,h,f){return t(),n("div",null,r)}const m=e(a,[["render",s]]);export{g as __pageData,m as default};
