import{_ as e,o as t,c as o,O as r}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 45.2-1915  Powers and duties of the Board; report.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/45.2-1915.md","filePath":"vacode/45.2-1915.md"}'),a={name:"vacode/45.2-1915.md"},i=r('<h1 id="§-45-2-1915-powers-and-duties-of-the-board-report" tabindex="-1">§ 45.2-1915 Powers and duties of the Board; report. <a class="header-anchor" href="#§-45-2-1915-powers-and-duties-of-the-board-report" aria-label="Permalink to &quot;§ 45.2-1915  Powers and duties of the Board; report.&quot;">​</a></h1><p>The Board has the powers and duties to:</p><ol><li><p>Advise the Director on the management of the Fund pursuant to the provisions of § <a href="/vacode/45.2-1916/">45.2-1916</a>;</p></li><li><p>Develop, establish, and operate, with the approval of the Director, the Program pursuant to the provisions of § <a href="/vacode/45.2-1917/">45.2-1917</a>;</p></li><li><p>Advise the Director on the possibility of working with a community development financial institution or other financial institutions to further the purposes of the Program;</p></li><li><p>Advise the Director on the distribution of moneys in the Fund in the form of loans or rebates pursuant to the provisions of § <a href="/vacode/45.2-1917/">45.2-1917</a>; and</p></li><li><p>Submit to the Governor and the General Assembly an annual report for publication as a report document as provided in the procedures of the Division of Legislative Automated Systems for the processing of legislative documents and reports. The chairman shall submit to the Governor and the General Assembly an annual executive summary of the interim activity and work of the Board no later than the first day of each regular session of the General Assembly. The executive summary shall be submitted for publication as a report document as provided in the procedures of the Division of Legislative Automated Systems for the processing of legislative documents and reports and shall be posted on the General Assembly&#39;s website.</p></li></ol><p>2019, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0554" target="_blank" rel="noreferrer">554</a>, § 45.1-397; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0387" target="_blank" rel="noreferrer">387</a>.</p>',4),s=[i];function n(d,h,l,p,c,f){return t(),o("div",null,s)}const v=e(a,[["render",n]]);export{m as __pageData,v as default};
