import{_ as e,o as a,c as r,O as t}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 45.2-2101  Nuclear energy; strategic plan.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/45.2-2101.md","filePath":"vacode/45.2-2101.md"}'),n={name:"vacode/45.2-2101.md"},o=t('<h1 id="§-45-2-2101-nuclear-energy-strategic-plan" tabindex="-1">§ 45.2-2101 Nuclear energy; strategic plan. <a class="header-anchor" href="#§-45-2-2101-nuclear-energy-strategic-plan" aria-label="Permalink to &quot;§ 45.2-2101  Nuclear energy; strategic plan.&quot;">​</a></h1><p>A. The Department and the Secretaries of Commerce and Trade and Education shall work in coordination with the Authority, established pursuant to Article 2 (§ <a href="/vacode/45.2-2102/">45.2-2102</a>), and the Virginia Economic Development Partnership Authority, established pursuant to Article 4 (§ <a href="/vacode/2.2-2234/">2.2-2234</a> et seq.) of Chapter 22 of Title 2.2, to develop a strategic plan for nuclear energy as part of the Commonwealth&#39;s overall goal of carbon-free energy.</p><p>B. Such plan may include (i) the promotion of new technologies and opportunities for innovation, including advanced manufacturing; (ii) the establishment of a collaborative research center and university nuclear leadership program to promote education in fields that meet the workforce demands of Virginia&#39;s nuclear industry; and (iii) recognition of the role of nuclear energy in the Commonwealth&#39;s goal of employing 100 percent carbon-free sources of energy by 2050.</p><p>C. Such plan shall be completed by October 1, 2020, updated every four years thereafter, and published on the Internet by the Authority.</p><p>2020, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0657" target="_blank" rel="noreferrer">657</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0658" target="_blank" rel="noreferrer">658</a>, § 67-1700; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0387" target="_blank" rel="noreferrer">387</a>.</p>',5),i=[o];function c(l,s,p,h,d,f){return a(),r("div",null,i)}const _=e(n,[["render",c]]);export{u as __pageData,_ as default};
