import{_ as e,o as t,c as r,O as o}from"./chunks/framework.9482e208.js";const p=JSON.parse('{"title":"§ 45.2-2106  Moneys of Authority.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/45.2-2106.md","filePath":"vacode/45.2-2106.md"}'),a={name:"vacode/45.2-2106.md"},s=o('<h1 id="§-45-2-2106-moneys-of-authority" tabindex="-1">§ 45.2-2106 Moneys of Authority. <a class="header-anchor" href="#§-45-2-2106-moneys-of-authority" aria-label="Permalink to &quot;§ 45.2-2106  Moneys of Authority.&quot;">​</a></h1><p>All moneys of the Authority, from whatever source derived, shall be paid to the treasurer of the Authority. Such moneys shall be deposited in the first instance by the treasurer in one or more banks or trust companies, in one or more special accounts. All banks and trust companies are authorized to give such security for such deposits, if required by the Authority. The moneys in such accounts shall be paid out on the warrant or other orders of such persons as the Authority authorizes to execute such warrants or orders.</p><p>2013, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0057" target="_blank" rel="noreferrer">57</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0394" target="_blank" rel="noreferrer">394</a>, § 67-1405; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0387" target="_blank" rel="noreferrer">387</a>.</p>',3),i=[s];function n(h,c,l,u,_,d){return t(),r("div",null,i)}const m=e(a,[["render",n]]);export{p as __pageData,m as default};
