import{_ as e,o as t,c as a,O as r}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 45.2-2107  Audits; external reviews.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/45.2-2107.md","filePath":"vacode/45.2-2107.md"}'),i={name:"vacode/45.2-2107.md"},o=r('<h1 id="§-45-2-2107-audits-external-reviews" tabindex="-1">§ 45.2-2107 Audits; external reviews. <a class="header-anchor" href="#§-45-2-2107-audits-external-reviews" aria-label="Permalink to &quot;§ 45.2-2107  Audits; external reviews.&quot;">​</a></h1><p>A. The Auditor of Public Accounts, or his legally authorized representatives, shall annually audit the financial accounts of the Authority. The audit report and any nonproprietary information provided to the auditor in connection with the audit shall be made available to the public, upon request, in accordance with the provisions of the Virginia Freedom of Information Act (§ <a href="/vacode/2.2-3700/">2.2-3700</a> et seq.).</p><p>B. The Authority, if it receives state funds, shall be subject to periodic external review either (i) under the provisions of the Legislative Program Review and Evaluation Act (§ <a href="/vacode/30-64/">30-64</a> et seq.) or (ii) by an entity appointed for that purpose by the Governor.</p><p>2013, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0057" target="_blank" rel="noreferrer">57</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0394" target="_blank" rel="noreferrer">394</a>, § 67-1406; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0387" target="_blank" rel="noreferrer">387</a>.</p>',4),n=[o];function s(l,c,d,h,p,u){return t(),a("div",null,n)}const v=e(i,[["render",s]]);export{f as __pageData,v as default};
