import{_ as e,o as a,c as t,O as o}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 45.2-2119  Civil penalty.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/45.2-2119.md","filePath":"vacode/45.2-2119.md"}'),i={name:"vacode/45.2-2119.md"},r=o('<h1 id="§-45-2-2119-civil-penalty" tabindex="-1">§ 45.2-2119 Civil penalty. <a class="header-anchor" href="#§-45-2-2119-civil-penalty" aria-label="Permalink to &quot;§ 45.2-2119  Civil penalty.&quot;">​</a></h1><p>A. Any person who violates any provision of this article, or who fails, neglects, or refuses to comply with any regulation adopted by the Director or final order of a court lawfully issued, shall be subject to a civil penalty not to exceed $10,000 for each violation. Each day of violation shall constitute a separate offense. All civil penalties shall be paid into the state treasury and deposited by the State Treasurer into the Minerals Reclamation Fund pursuant to § <a href="/vacode/45.2-1234/">45.2-1234</a>.</p><p>B. The Director may restrain violations of this article in accordance with the provisions of § <a href="/vacode/45.2-1608/">45.2-1608</a>.</p><p>1982, c. 269, § 45.1-282; 1984, c. 590; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0387" target="_blank" rel="noreferrer">387</a>.</p>',4),n=[r];function l(s,c,p,d,h,_){return a(),t("div",null,n)}const u=e(i,[["render",l]]);export{v as __pageData,u as default};
