import{_ as e,o as t,c as i,O as o}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 45.2-505  Prohibited acts by miners or other persons; miners to comply with law.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/45.2-505.md","filePath":"vacode/45.2-505.md"}'),r={name:"vacode/45.2-505.md"},a=o('<h1 id="§-45-2-505-prohibited-acts-by-miners-or-other-persons-miners-to-comply-with-law" tabindex="-1">§ 45.2-505 Prohibited acts by miners or other persons; miners to comply with law. <a class="header-anchor" href="#§-45-2-505-prohibited-acts-by-miners-or-other-persons-miners-to-comply-with-law" aria-label="Permalink to &quot;§ 45.2-505  Prohibited acts by miners or other persons; miners to comply with law.&quot;">​</a></h1><p>A. No miner or other person shall (i) knowingly damage any shaft, lamp, instrument, air course, or brattice or obstruct any airway; (ii) carry in a mine any intoxicating liquors or controlled drugs without the prescription of a licensed physician; (iii) disturb any part of the machinery or appliances in a mine; (iv) open a door used for directing ventilation and fail to close it again; (v) enter any part of a mine against caution or a warning sign or barricade; or (vi) disobey any order issued pursuant to the provisions of the Act.</p><p>B. Each miner at any mine shall comply fully with the provisions of the Act and other mining laws of the Commonwealth, including regulations adopted by the Department or the Board, that pertain to his duties.</p><p>C. Any individual shall, upon the order of the Chief, complete training that addresses the subject of any violation issued to the individual as a condition for abatement of the violation.</p><p>Code 1950, § 45-72; 1954, c. 191; 1966, c. 594, § 45.1-26; 1978, cc. 489, 729; 1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0028" target="_blank" rel="noreferrer">28</a>, § 45.1-161.12; 2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0003" target="_blank" rel="noreferrer">3</a>; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0387" target="_blank" rel="noreferrer">387</a>.</p>',5),n=[a];function s(c,l,h,p,d,m){return t(),i("div",null,n)}const u=e(r,[["render",s]]);export{_ as __pageData,u as default};
