import{_ as e,o as a,c as o,O as n}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 45.2-517  Board compensation; expenses.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/45.2-517.md","filePath":"vacode/45.2-517.md"}'),r={name:"vacode/45.2-517.md"},s=n('<h1 id="§-45-2-517-board-compensation-expenses" tabindex="-1">§ 45.2-517 Board compensation; expenses. <a class="header-anchor" href="#§-45-2-517-board-compensation-expenses" aria-label="Permalink to &quot;§ 45.2-517  Board compensation; expenses.&quot;">​</a></h1><p>Nonlegislative citizen members of the Board of Coal Mining Examiners shall receive such compensation for the performance of their duties as provided in § <a href="/vacode/2.2-2813/">2.2-2813</a>. All such nonlegislative citizen members shall be reimbursed for all reasonable and necessary expenses incurred in the performance of their duties as provided in §§ <a href="/vacode/2.2-2813/">2.2-2813</a> and <a href="/vacode/2.2-2825/">2.2-2825</a>. Funding for the costs of compensation and expenses of such members shall be provided by the Coal Mining Examiners&#39; Fund established in § <a href="/vacode/45.2-523/">45.2-523</a>.</p><p>Code 1950, § 45-26; 1954, c. 191; 1960, c. 61; 1966, c. 594, § 45.1-10; 1978, c. 120; 1980, c. 728; 1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0028" target="_blank" rel="noreferrer">28</a>, § 45.1-161.25; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0387" target="_blank" rel="noreferrer">387</a>.</p>',3),i=[s];function t(c,d,l,p,h,_){return a(),o("div",null,i)}const v=e(r,[["render",t]]);export{m as __pageData,v as default};
