import{_ as e,o,c as t,O as a}from"./chunks/framework.9482e208.js";const p=JSON.parse('{"title":"§ 45.2-519  Nominations for the Board.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/45.2-519.md","filePath":"vacode/45.2-519.md"}'),n={name:"vacode/45.2-519.md"},r=a('<h1 id="§-45-2-519-nominations-for-the-board" tabindex="-1">§ 45.2-519 Nominations for the Board. <a class="header-anchor" href="#§-45-2-519-nominations-for-the-board" aria-label="Permalink to &quot;§ 45.2-519  Nominations for the Board.&quot;">​</a></h1><p>Nominations for appointments to the Board of Coal Mining Examiners may be submitted to the Governor by the Director and each organization of coal miners and coal industry interests in the Commonwealth. Nominations are to be made to the Governor by June 1 of the year in which the terms of appointments of members expire. In no case shall the Governor be bound to make any appointment from the nominations submitted.</p><p>Code 1950, §§ 45-4, 45-5, 45-6; 1954, c. 191; 1966, c. 594, § 45.1-4; 1972, c. 784; 1978, cc. 120, 727; 1981, c. 32; 1984, cc. 184, 337, 590; 1987, Sp. Sess., c. 1; 1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0028" target="_blank" rel="noreferrer">28</a>, § 45.1-161.19; 1997, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0390" target="_blank" rel="noreferrer">390</a>; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0387" target="_blank" rel="noreferrer">387</a>.</p>',3),i=[r];function s(c,h,l,m,d,_){return o(),t("div",null,i)}const b=e(n,[["render",s]]);export{p as __pageData,b as default};
