import{_ as e,o as a,c as i,O as r}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 45.2-530  General coal miner certification.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/45.2-530.md","filePath":"vacode/45.2-530.md"}'),t={name:"vacode/45.2-530.md"},n=r('<h1 id="§-45-2-530-general-coal-miner-certification" tabindex="-1">§ 45.2-530 General coal miner certification. <a class="header-anchor" href="#§-45-2-530-general-coal-miner-certification" aria-label="Permalink to &quot;§ 45.2-530  General coal miner certification.&quot;">​</a></h1><p>A. Every person working in a coal mine in the Commonwealth shall hold a general coal miner certificate issued by the Board of Coal Mining Examiners. The Board of Coal Mining Examiners shall issue a general coal miner certificate upon submittal of a complete application.</p><p>B. Each applicant for a general coal miner certificate who has not been employed to work in a Virginia coal mine prior to January 1, 1996, shall prove to the Board that he has knowledge of first aid practices and has a general working knowledge of the provisions of the Act, and applicable regulations, pertaining to coal mining health and safety. Each applicant shall have completed the miner training requirements of 30 C.F.R. Part 48 or submit proof of at least one year of experience in a coal mine prior to issuance of the general coal miner certificate.</p><p>1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0028" target="_blank" rel="noreferrer">28</a>, § 45.1-161.37; 2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0003" target="_blank" rel="noreferrer">3</a>; 2007, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0894" target="_blank" rel="noreferrer">894</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0914" target="_blank" rel="noreferrer">914</a>; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0387" target="_blank" rel="noreferrer">387</a>.</p>',4),o=[n];function l(c,s,p,f,h,g){return a(),i("div",null,o)}const d=e(t,[["render",l]]);export{_ as __pageData,d as default};
