import{_ as e,o as a,c as t,O as n}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 45.2-539  Operating without license; penalty.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/45.2-539.md","filePath":"vacode/45.2-539.md"}'),r={name:"vacode/45.2-539.md"},i=n('<h1 id="§-45-2-539-operating-without-license-penalty" tabindex="-1">§ 45.2-539 Operating without license; penalty. <a class="header-anchor" href="#§-45-2-539-operating-without-license-penalty" aria-label="Permalink to &quot;§ 45.2-539  Operating without license; penalty.&quot;">​</a></h1><p>A. In addition to any other power conferred by law, the Chief or his designated representative may issue an order closing any coal mine that is operating without a license. The procedure for issuing a closure order shall be as provided in § <a href="/vacode/45.2-569/">45.2-569</a>.</p><p>B. Any person operating an unlicensed mine is guilty of a Class 3 misdemeanor. Each day any person operates an unlicensed mine shall constitute a separate offense.</p><p>Code 1950, § 45-17.4; 1958, c. 306; 1966, c. 594, § 45.1-25; 1978, c. 489; 1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0028" target="_blank" rel="noreferrer">28</a>, § 45.1-161.61; 1997, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0390" target="_blank" rel="noreferrer">390</a>; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0387" target="_blank" rel="noreferrer">387</a>.</p>',4),o=[i];function s(l,c,p,d,h,_){return a(),t("div",null,o)}const u=e(r,[["render",s]]);export{f as __pageData,u as default};
