import{_ as e,o as a,c as t,O as r}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 45.2-540  Annual reports; condition to issuance of license following transfer of ownership.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/45.2-540.md","filePath":"vacode/45.2-540.md"}'),n={name:"vacode/45.2-540.md"},o=r('<h1 id="§-45-2-540-annual-reports-condition-to-issuance-of-license-following-transfer-of-ownership" tabindex="-1">§ 45.2-540 Annual reports; condition to issuance of license following transfer of ownership. <a class="header-anchor" href="#§-45-2-540-annual-reports-condition-to-issuance-of-license-following-transfer-of-ownership" aria-label="Permalink to &quot;§ 45.2-540  Annual reports; condition to issuance of license following transfer of ownership.&quot;">​</a></h1><p>A. The operator of each mine or his agent shall annually, by February 15, mail or deliver to the Department a report for the preceding 12 months ending with December 31. Such report shall state (i) the names of the operator, any agent, and any officers of the mine; (ii) the amount of coal mined; and (iii) other information, not of a private nature, that from time to time is required by the Department on forms furnished or approved by the Department.</p><p>B. Whenever the owner of a mine transfers the ownership of such mine to another person, the person transferring such ownership shall submit a report to the Department of such change and a statement of the tons of coal produced since the January 1 previous to the date of such sale or transfer of such mine. A license shall not be issued covering such transfer of ownership until the report is furnished.</p><p>C. The operator of each coal mine or his agent shall annually, by February 15, mail or deliver to the Department (i) an affidavit, certified by the commissioner of the revenue of the locality in which the coal mining operations are conducted, stating that all local coal severance taxes enacted pursuant to §§ <a href="/vacode/58.1-3703/">58.1-3703</a>, <a href="/vacode/58.1-3712/">58.1-3712</a>, <a href="/vacode/58.1-3713/">58.1-3713</a>, and <a href="/vacode/58.1-3741/">58.1-3741</a> due with respect to the coal mining operations have been paid and (ii) an affidavit, certified by the Treasurer of the locality in which the coal mining operations are conducted, stating that all personal property, real estate, and mineral land taxes due with respect to coal mining operations have been paid.</p><p>Code 1950, §§ 45-7, 45-12, 45-68.4, 45-69, 45-73, 45-75, 45-78, 45-79, 45-81, 45-83; 1950, p. 156; 1954, c. 191; 1966, c. 594, § 45.1-21; 1976, c. 598; 1978, cc. 222, 489; 1982, c. 255; 1984, cc. 178, 590; 1988, c. 577; 1993, cc. 171, 442; 1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0028" target="_blank" rel="noreferrer">28</a>, § 45.1-161.62; 1996, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0774" target="_blank" rel="noreferrer">774</a>; 1997, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0390" target="_blank" rel="noreferrer">390</a>; 2013, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0305" target="_blank" rel="noreferrer">305</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0618" target="_blank" rel="noreferrer">618</a>; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0387" target="_blank" rel="noreferrer">387</a>.</p>',5),i=[o];function s(c,l,h,f,p,d){return a(),t("div",null,i)}const m=e(n,[["render",s]]);export{g as __pageData,m as default};
