import{_ as e,o as r,c as t,O as i}from"./chunks/framework.9482e208.js";const f=JSON.parse(`{"title":"§ 45.2-554  Workers' compensation; liability.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/45.2-554.md","filePath":"vacode/45.2-554.md"}`),o={name:"vacode/45.2-554.md"},a=i('<h1 id="§-45-2-554-workers-compensation-liability" tabindex="-1">§ 45.2-554 Workers&#39; compensation; liability. <a class="header-anchor" href="#§-45-2-554-workers-compensation-liability" aria-label="Permalink to &quot;§ 45.2-554  Workers&#39; compensation; liability.&quot;">​</a></h1><p>A. For the purpose of workers&#39; compensation coverage, during any mine disaster to which a state-designated mine rescue team responds under the provisions of this article or during any training exercise for a state-designated mine rescue team, members of the state-designated team shall be deemed to be within the employment of the operator of the mine at which the disaster occurred or the training exercise is conducted. Additionally, for purposes of workers&#39; compensation coverage, travel by members of a state-designated mine rescue team to and from the mine disaster or training exercise shall be deemed to be within the employment of the operator of the mine at which the disaster occurred or the training exercise is conducted.</p><p>B. No member of a state-designated mine rescue team engaging in rescue work at a mine shall be liable for civil damages for acts or omissions resulting from the rendering of such rescue work unless the act or omission was the result of gross negligence or willful misconduct.</p><p>C. No operator providing personnel to a state-designated mine rescue team to engage in rescue work at a mine not owned or operated by the operator shall be liable for any civil damages for acts or omissions resulting from the rendering of such rescue work.</p><p>1985, c. 496, § 45.1-33.5:4; 1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0028" target="_blank" rel="noreferrer">28</a>, § 45.1-161.76; 2007, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0894" target="_blank" rel="noreferrer">894</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0914" target="_blank" rel="noreferrer">914</a>; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0387" target="_blank" rel="noreferrer">387</a>.</p>',5),s=[a];function n(c,l,d,m,h,g){return r(),t("div",null,s)}const u=e(o,[["render",n]]);export{f as __pageData,u as default};
