import{_ as e,o as a,c as r,O as n}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 45.2-555  Reports of explosions and mine fires; procedure.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/45.2-555.md","filePath":"vacode/45.2-555.md"}'),o={name:"vacode/45.2-555.md"},t=n('<h1 id="§-45-2-555-reports-of-explosions-and-mine-fires-procedure" tabindex="-1">§ 45.2-555 Reports of explosions and mine fires; procedure. <a class="header-anchor" href="#§-45-2-555-reports-of-explosions-and-mine-fires-procedure" aria-label="Permalink to &quot;§ 45.2-555  Reports of explosions and mine fires; procedure.&quot;">​</a></h1><p>A. If an explosion or mine fire occurs in a mine, the operator shall notify the Department by the quickest available means. All facilities of the mine shall be made available for rescue and recovery operations and firefighting.</p><p>B. No work other than rescue and recovery work and firefighting shall be attempted unless authorized by the Department.</p><p>C. If an explosion occurs in an underground coal mine, the fan shall not be reversed except by authority of the officials in charge of rescue and recovery work, and then only after a study of the effect of reversing the fan on any person who may have survived the explosion and is still underground.</p><p>D. The Department shall make available all the facilities at its disposal in effecting rescue and recovery work. The Chief shall act as consultant, or take personal charge, where in his opinion the circumstances of any mine explosion, fire, or other accident warrant.</p><p>E. The orders of the official in charge of rescue and recovery work shall be respected and obeyed by all persons engaged in rescue and recovery work.</p><p>F. The Chief shall maintain an up-to-date rescue and recovery plan for prompt and adequate employment at any coal mine in the Commonwealth. All employees of the Department shall be kept fully informed and trained in their respective duties in executing rescue and recovery plans. The Department&#39;s plan shall be reviewed annually. Any changes in the plan shall be published promptly and made available to all operators of mines.</p><p>Code 1950, § 45-14; 1954, c. 191; 1966, c. 594, § 45.1-6; 1984, c. 590; 1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0028" target="_blank" rel="noreferrer">28</a>, § 45.1-161.77; 1997, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0390" target="_blank" rel="noreferrer">390</a>; 1999, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0256" target="_blank" rel="noreferrer">256</a>; 2011, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0826" target="_blank" rel="noreferrer">826</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0862" target="_blank" rel="noreferrer">862</a>; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0387" target="_blank" rel="noreferrer">387</a>.</p>',8),i=[t];function l(s,c,p,h,f,d){return a(),r("div",null,i)}const m=e(o,[["render",l]]);export{g as __pageData,m as default};
