import{_ as e,o as n,c as t,O as i}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 45.2-563  Scheduling of mine inspections.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/45.2-563.md","filePath":"vacode/45.2-563.md"}'),a={name:"vacode/45.2-563.md"},r=i('<h1 id="§-45-2-563-scheduling-of-mine-inspections" tabindex="-1">§ 45.2-563 Scheduling of mine inspections. <a class="header-anchor" href="#§-45-2-563-scheduling-of-mine-inspections" aria-label="Permalink to &quot;§ 45.2-563  Scheduling of mine inspections.&quot;">​</a></h1><p>A. The Chief and the Director shall schedule the inspections of mines under this article, to the extent deemed reasonable and prudent, in order to reduce their chronological proximity to inspections conducted by MSHA.</p><p>B. The Chief, the Director, and each mine inspector, to the extent deemed reasonable and prudent, shall schedule mine inspections to commence at a variety of hours of the day and days of the week, including evening and night shifts, weekends, and holidays.</p><p>1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0028" target="_blank" rel="noreferrer">28</a>, § 45.1-161.85; 2011, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0826" target="_blank" rel="noreferrer">826</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0862" target="_blank" rel="noreferrer">862</a>; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0387" target="_blank" rel="noreferrer">387</a>.</p>',4),o=[r];function s(c,l,d,h,p,f){return n(),t("div",null,o)}const u=e(a,[["render",s]]);export{g as __pageData,u as default};
