import{_ as e,o as n,c as r,O as o}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 45.2-600  Consent required before working mine near land of another.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/45.2-600.md","filePath":"vacode/45.2-600.md"}'),t={name:"vacode/45.2-600.md"},a=o('<h1 id="§-45-2-600-consent-required-before-working-mine-near-land-of-another" tabindex="-1">§ 45.2-600 Consent required before working mine near land of another. <a class="header-anchor" href="#§-45-2-600-consent-required-before-working-mine-near-land-of-another" aria-label="Permalink to &quot;§ 45.2-600  Consent required before working mine near land of another.&quot;">​</a></h1><p>No owner or tenant of any land containing coal within the Commonwealth shall open or sink, dig, excavate, or work in any mine on such land within five feet of the line dividing such land from that of another person without the written consent of every person interested in or having title to such adjoining lands or mineral rights in possession, reversion, or remainder, or of the guardian of any such person if the person is under a disability. Any person who violates this section shall forfeit $500 to any person injured by such activity and to anyone whose consent is required but not obtained.</p><p>Code 1950, § 45-91; 1966, c. 594, § 45.1-102; 1978, c. 118; 1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0028" target="_blank" rel="noreferrer">28</a>, § 45.1-161.310; 1997, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0390" target="_blank" rel="noreferrer">390</a>; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0387" target="_blank" rel="noreferrer">387</a>.</p>',3),i=[a];function s(l,d,c,h,f,_){return n(),r("div",null,i)}const u=e(t,[["render",s]]);export{g as __pageData,u as default};
