import{_ as e,o,c as t,O as a}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 45.2-704  Employment and duties of top persons; plan for excavation of shaft or slope.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/45.2-704.md","filePath":"vacode/45.2-704.md"}'),n={name:"vacode/45.2-704.md"},i=a('<h1 id="§-45-2-704-employment-and-duties-of-top-persons-plan-for-excavation-of-shaft-or-slope" tabindex="-1">§ 45.2-704 Employment and duties of top persons; plan for excavation of shaft or slope. <a class="header-anchor" href="#§-45-2-704-employment-and-duties-of-top-persons-plan-for-excavation-of-shaft-or-slope" aria-label="Permalink to &quot;§ 45.2-704  Employment and duties of top persons; plan for excavation of shaft or slope.&quot;">​</a></h1><p>A. During the construction or modification of any shaft or slope mine, the person engaged in the actual construction or modification of such mine shall employ one or more top persons certified pursuant to § <a href="/vacode/45.2-534/">45.2-534</a>. It is the duty of such top person to examine for proper and safe practices and materials used during the construction or modification of a shaft or slope mine. Such duties shall at all times be performed in the immediate vicinity of the shaft or slope under construction.</p><p>B. Prior to commencing the excavation of any shaft or slope, the operator shall submit to the Department a copy of the plan that includes the following: (i) the name and location of the mine and shaft or slope; (ii) a description of the work and methods to be used in the construction of the shaft or slope; (iii) a description of the methods to be used to ensure wall and roof stability; (iv) a description of the system of ventilation to be used, including procedures for evacuation of the shaft or slope if a fan stoppage occurs; (v) details of hoisting equipment to be used; and (vi) such other information as the Chief requires. The excavation of such shaft or slope shall not begin until the plan is approved by the Chief.</p><p>1980, c. 442, § 45.1-20.1; 1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0028" target="_blank" rel="noreferrer">28</a>, § 45.1-161.250; 2011, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0826" target="_blank" rel="noreferrer">826</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0862" target="_blank" rel="noreferrer">862</a>; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0387" target="_blank" rel="noreferrer">387</a>.</p>',4),r=[i];function s(p,l,f,c,h,d){return o(),t("div",null,r)}const _=e(n,[["render",s]]);export{m as __pageData,_ as default};
