import{_ as e,o as r,c as n,O as o}from"./chunks/framework.9482e208.js";const h=JSON.parse('{"title":"§ 45.2-705  Employment of inexperienced underground miners.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/45.2-705.md","filePath":"vacode/45.2-705.md"}'),t={name:"vacode/45.2-705.md"},a=o('<h1 id="§-45-2-705-employment-of-inexperienced-underground-miners" tabindex="-1">§ 45.2-705 Employment of inexperienced underground miners. <a class="header-anchor" href="#§-45-2-705-employment-of-inexperienced-underground-miners" aria-label="Permalink to &quot;§ 45.2-705  Employment of inexperienced underground miners.&quot;">​</a></h1><p>A. An inexperienced underground miner shall be required to work with an experienced underground miner for a total of at least six months following the start of underground employment. However, an experienced surface miner shall only be required to work with an experienced underground miner for a total of at least 60 days following the start of underground employment.</p><p>B. No inexperienced underground miner shall be assigned, allowed, or required to perform work alone in any area where there is a potential danger to his safety unless he can communicate with others or be heard or seen.</p><p>Code 1950, §§ 45-35, 45-40; 1954, c. 191; 1966, c. 594, § 45.1-17; 1978, c. 729; 1993, c. 170; 1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0028" target="_blank" rel="noreferrer">28</a>, § 45.1-161.251; 1999, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0256" target="_blank" rel="noreferrer">256</a>; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0387" target="_blank" rel="noreferrer">387</a>.</p>',4),i=[a];function d(l,s,c,p,m,u){return r(),n("div",null,i)}const _=e(t,[["render",d]]);export{h as __pageData,_ as default};
