import{_ as e,o,c as r,O as a}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 45.2-709  Roof, face, and ribs to be secure.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/45.2-709.md","filePath":"vacode/45.2-709.md"}'),t={name:"vacode/45.2-709.md"},n=a('<h1 id="§-45-2-709-roof-face-and-ribs-to-be-secure" tabindex="-1">§ 45.2-709 Roof, face, and ribs to be secure. <a class="header-anchor" href="#§-45-2-709-roof-face-and-ribs-to-be-secure" aria-label="Permalink to &quot;§ 45.2-709  Roof, face, and ribs to be secure.&quot;">​</a></h1><p>A. All underground active workings and travel ways shall be secured and controlled to protect miners from a fall of roof, face, or ribs. Loose roof and any loose or overhanging ribs or face shall be taken down or supported.</p><p>B. The mining method that the mine operator follows shall not expose any miner to a hazardous condition caused by the excessive width of a room or entry, a faulty pillar-recovery method, or any other hazardous mining method or working condition.</p><p>Code 1950, §§ 45-68.1 to 45-68.3; 1954, c. 191; 1966, c. 594, §§ 45.1-40, 45.1-41; 1975, c. 520; 1978, cc. 118, 729; 1980, c. 338; 1982, c. 234; 1984, c. 590; 1988, c. 577; 1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0028" target="_blank" rel="noreferrer">28</a>, § 45.1-161.108; 1996, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0774" target="_blank" rel="noreferrer">774</a>; 1999, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0256" target="_blank" rel="noreferrer">256</a>; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0387" target="_blank" rel="noreferrer">387</a>.</p>',4),i=[n];function c(s,l,d,f,h,_){return o(),r("div",null,i)}const b=e(t,[["render",c]]);export{g as __pageData,b as default};
