import{_ as e,o as a,c as t,O as l}from"./chunks/framework.9482e208.js";const b=JSON.parse('{"title":"§ 45.2-723  Blasting cables.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/45.2-723.md","filePath":"vacode/45.2-723.md"}'),r={name:"vacode/45.2-723.md"},s=l('<h1 id="§-45-2-723-blasting-cables" tabindex="-1">§ 45.2-723 Blasting cables. <a class="header-anchor" href="#§-45-2-723-blasting-cables" aria-label="Permalink to &quot;§ 45.2-723  Blasting cables.&quot;">​</a></h1><p>Each blasting cable shall be:</p><ol><li><p>Well insulated and as long as necessary to allow the shot firer to move to a safe place around a corner;</p></li><li><p>Short-circuited at the battery end until it is ready to attach to the blasting unit;</p></li><li><p>Staggered as to length, or shall have its ends kept well separated when attached to the detonator leg wires; and</p></li><li><p>Kept clear of power wires and all other possible sources of active or stray electric currents.</p></li></ol><p>Code 1950, § 45-53.6; 1954, c. 191; 1966, c. 594, § 45.1-49; 1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0028" target="_blank" rel="noreferrer">28</a>, § 45.1-161.130; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0387" target="_blank" rel="noreferrer">387</a>.</p>',4),o=[s];function i(n,c,p,d,_,h){return a(),t("div",null,o)}const f=e(r,[["render",i]]);export{b as __pageData,f as default};
