import{_ as e,o as a,c as t,O as o}from"./chunks/framework.9482e208.js";const h=JSON.parse('{"title":"§ 45.2-727  Separation of openings.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/45.2-727.md","filePath":"vacode/45.2-727.md"}'),r={name:"vacode/45.2-727.md"},n=o('<h1 id="§-45-2-727-separation-of-openings" tabindex="-1">§ 45.2-727 Separation of openings. <a class="header-anchor" href="#§-45-2-727-separation-of-openings" aria-label="Permalink to &quot;§ 45.2-727  Separation of openings.&quot;">​</a></h1><p>A. In a drift or slope mine, openings shall be separated by at least 50 feet of natural strata, unless specifically approved in the roof control plan. All connections between openings not used for the coursing of air, travel, or haulage shall be closed with stoppings of fireproof material.</p><p>B. In a shaft mine, openings shall be separated by at least 200 feet of natural strata.</p><p>Code 1950, § 45-71; 1954, c. 191; 1966, c. 594, § 45.1-94; 1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0028" target="_blank" rel="noreferrer">28</a>, § 45.1-161.163; 2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0003" target="_blank" rel="noreferrer">3</a>; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0387" target="_blank" rel="noreferrer">387</a>.</p>',4),i=[n];function s(l,p,c,f,_,g){return a(),t("div",null,i)}const u=e(r,[["render",s]]);export{h as __pageData,u as default};
