import{_ as e,o as a,c as t,O as r}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 45.2-742  Operations of hoisting equipment.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/45.2-742.md","filePath":"vacode/45.2-742.md"}'),i={name:"vacode/45.2-742.md"},n=r('<h1 id="§-45-2-742-operations-of-hoisting-equipment" tabindex="-1">§ 45.2-742 Operations of hoisting equipment. <a class="header-anchor" href="#§-45-2-742-operations-of-hoisting-equipment" aria-label="Permalink to &quot;§ 45.2-742  Operations of hoisting equipment.&quot;">​</a></h1><p>A. The speed of the cage, car, or trip in a shaft or slope or on a surface incline shall not exceed 1,000 feet per minute when a miner is being hoisted or lowered.</p><p>B. When moving the platform or work deck, every miner traveling thereon shall have a safety belt secured.</p><p>C. No miner shall ride on a loaded cage.</p><p>D. The number of miners riding in any cage or car at one time shall not exceed the maximum prescribed by the manufacturer. The Chief may prescribe a lesser number when necessary to ensure the safety of miners being transported.</p><p>E. Any conveyance being lowered into a shaft in which a miner is working shall be stopped at least 20 feet above the area where such miner is working.</p><p>F. If any miner is working at the bottom of a shaft, there shall be an adjustable ladder or chain ladder attached to the work deck to provide an additional means of escape. Such ladder shall be at least 20 feet in length.</p><p>G. Every choker or sling used to transport materials within a shaft or slope shall meet specifications established by the American National Standards Institute.</p><p>Code 1950, § 45-68.4; 1954, c. 191; 1966, c. 594, §§ 45.1-68, 45.1-69; 1972, c. 784; 1978, c. 729; 1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0028" target="_blank" rel="noreferrer">28</a>, § 45.1-161.160; 1996, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0774" target="_blank" rel="noreferrer">774</a>; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0387" target="_blank" rel="noreferrer">387</a>.</p>',9),o=[n];function s(l,h,c,p,d,f){return a(),t("div",null,o)}const _=e(i,[["render",s]]);export{g as __pageData,_ as default};
