import{_ as e,o as a,c as r,O as t}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 45.2-745  Track switches and rails.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/45.2-745.md","filePath":"vacode/45.2-745.md"}'),i={name:"vacode/45.2-745.md"},s=t('<h1 id="§-45-2-745-track-switches-and-rails" tabindex="-1">§ 45.2-745 Track switches and rails. <a class="header-anchor" href="#§-45-2-745-track-switches-and-rails" aria-label="Permalink to &quot;§ 45.2-745  Track switches and rails.&quot;">​</a></h1><p>A. Every track switch shall be provided with a properly installed throw and properly installed latches and bridle bars.</p><p>B. Every track switch, other than a switch in a room or entry development, shall be equipped with properly installed guardrails.</p><p>C. Every switch throw and stand shall be installed on the side of the track where clearance is provided.</p><p>D. Every rail shall be secured at all joints by plates or welds.</p><p>Code 1950, §§ 45-69.1, 45-69.4; 1954, c. 191; 1966, c. 594, §§ 45.1-70, 45.1-73; 1979, c. 315; 1983, c. 70; 1985, c. 302; 1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0028" target="_blank" rel="noreferrer">28</a>, § 45.1-161.134; 1999, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0256" target="_blank" rel="noreferrer">256</a>; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0387" target="_blank" rel="noreferrer">387</a>.</p>',6),l=[s];function c(n,o,d,h,p,_){return a(),r("div",null,l)}const g=e(i,[["render",c]]);export{v as __pageData,g as default};
