import{_ as e,o as n,c as t,O as r}from"./chunks/framework.9482e208.js";const h=JSON.parse('{"title":"§ 45.2-750  Inspection of underground equipment.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/45.2-750.md","filePath":"vacode/45.2-750.md"}'),o={name:"vacode/45.2-750.md"},a=r('<h1 id="§-45-2-750-inspection-of-underground-equipment" tabindex="-1">§ 45.2-750 Inspection of underground equipment. <a class="header-anchor" href="#§-45-2-750-inspection-of-underground-equipment" aria-label="Permalink to &quot;§ 45.2-750  Inspection of underground equipment.&quot;">​</a></h1><p>Once per week, or more often if necessary, the mine foreman or a certified person shall inspect electrical and diesel transportation equipment to ensure its safe operating condition. Such equipment located on the surface shall be inspected once per month, or more often if necessary. Such person shall correct any defect found during the inspection. A record of such examinations shall be maintained.</p><p>1966, c. 594, § 45.1-85; 1978, c. 118; 1993, c. 442; 1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0028" target="_blank" rel="noreferrer">28</a>, § 45.1-161.139; 1996, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0774" target="_blank" rel="noreferrer">774</a>; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0387" target="_blank" rel="noreferrer">387</a>.</p>',3),i=[a];function c(s,p,d,l,f,u){return n(),t("div",null,i)}const m=e(o,[["render",c]]);export{h as __pageData,m as default};
