import{_ as e,o as a,c as t,O as r}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 45.2-754  Transportation of material.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/45.2-754.md","filePath":"vacode/45.2-754.md"}'),o={name:"vacode/45.2-754.md"},n=r('<h1 id="§-45-2-754-transportation-of-material" tabindex="-1">§ 45.2-754 Transportation of material. <a class="header-anchor" href="#§-45-2-754-transportation-of-material" aria-label="Permalink to &quot;§ 45.2-754  Transportation of material.&quot;">​</a></h1><p>A. Any equipment, material, or supplies being transported shall be loaded in a manner that protects the operator and other personnel from sliding equipment, material, or supplies.</p><p>B. Any equipment, material, or supplies that are not necessary for the operation of a piece of self-propelled mobile equipment shall not be transported on such equipment, except for when the mobile equipment is designed to carry such materials or supplies and no hazard is created. Only small hand tools and materials or supplies that do not create hazards may be transported in the same compartment of a mantrip where any miner is seated.</p><p>Code 1950, § 45-69.1; 1954, c. 191; 1966, c. 594, § 45.1-73; 1979, c. 315; 1983, c. 70; 1985, c. 302; 1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0028" target="_blank" rel="noreferrer">28</a>, § 45.1-161.143; 1999, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0256" target="_blank" rel="noreferrer">256</a>; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0387" target="_blank" rel="noreferrer">387</a>.</p>',4),i=[n];function s(l,p,c,d,m,h){return a(),t("div",null,i)}const u=e(o,[["render",s]]);export{f as __pageData,u as default};
