import{_ as e,o as a,c as r,O as t}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 45.2-755  Securing cars.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/45.2-755.md","filePath":"vacode/45.2-755.md"}'),i={name:"vacode/45.2-755.md"},n=t('<h1 id="§-45-2-755-securing-cars" tabindex="-1">§ 45.2-755 Securing cars. <a class="header-anchor" href="#§-45-2-755-securing-cars" aria-label="Permalink to &quot;§ 45.2-755  Securing cars.&quot;">​</a></h1><p>A. A standing car on any track, unless it is held effectively by brakes, shall be properly blocked to prevent movement.</p><p>B. Positive-acting stopblocks or derails shall be used when necessary to protect miners from the hazard of runaway rail equipment. Derails shall be located where a grade at the entrance or any other location in the mine creates a potential collision hazard.</p><p>C. Safety chains, steel ropes, or other effective devices capable of holding the load shall be used to prevent a runaway mantrip or other supply car.</p><p>Code 1950, § 45-69.1; 1954, c. 191; 1966, c. 594, § 45.1-73; 1979, c. 315; 1983, c. 70; 1985, c. 302; 1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0028" target="_blank" rel="noreferrer">28</a>, § 45.1-161.144; 1996, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0774" target="_blank" rel="noreferrer">774</a>; 1999, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0256" target="_blank" rel="noreferrer">256</a>; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0387" target="_blank" rel="noreferrer">387</a>.</p>',5),o=[n];function c(s,l,p,h,d,_){return a(),r("div",null,o)}const u=e(i,[["render",c]]);export{f as __pageData,u as default};
