import{_ as e,o,c as a,O as r}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 45.2-756  Riding on cars.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/45.2-756.md","filePath":"vacode/45.2-756.md"}'),t={name:"vacode/45.2-756.md"},i=r('<h1 id="§-45-2-756-riding-on-cars" tabindex="-1">§ 45.2-756 Riding on cars. <a class="header-anchor" href="#§-45-2-756-riding-on-cars" aria-label="Permalink to &quot;§ 45.2-756  Riding on cars.&quot;">​</a></h1><p>A. No person other than the motorman and the trip rider shall ride on a locomotive unless authorized by the mine foreman.</p><p>B. No person shall ride on a loaded car or between cars of any trip.</p><p>C. No person shall get on or off a moving locomotive or a car that is being moved by a locomotive.</p><p>D. No person shall be allowed to ride on top of a piece of self-propelled mobile equipment.</p><p>Code 1950, § 45-69.1; 1954, c. 191; 1966, c. 594, § 45.1-73; 1979, c. 315; 1983, c. 70; 1985, c. 302; 1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0028" target="_blank" rel="noreferrer">28</a>, § 45.1-161.145; 1996, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0774" target="_blank" rel="noreferrer">774</a>; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0387" target="_blank" rel="noreferrer">387</a>.</p>',6),n=[i];function c(l,s,p,d,_,h){return o(),a("div",null,n)}const m=e(t,[["render",c]]);export{g as __pageData,m as default};
