import{_ as e,o as a,c as t,O as i}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 45.2-803  Surface electrical installations.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/45.2-803.md","filePath":"vacode/45.2-803.md"}'),r={name:"vacode/45.2-803.md"},n=i('<h1 id="§-45-2-803-surface-electrical-installations" tabindex="-1">§ 45.2-803 Surface electrical installations. <a class="header-anchor" href="#§-45-2-803-surface-electrical-installations" aria-label="Permalink to &quot;§ 45.2-803  Surface electrical installations.&quot;">​</a></h1><p>A. Any overhead high-potential power line shall be (i) placed at least 15 feet above the ground and 20 feet above any driveway, (ii) installed on insulators, and (iii) supported and guarded to prevent contact with other circuits.</p><p>B. Any surface transmission line, including a trolley circuit, shall be protected against short circuits and lightning. Each power circuit that leads underground shall be equipped with lightning arrestors within 100 feet of the location at which the circuit enters the mine.</p><p>C. Electric wiring in any surface building shall be installed so as to prevent fire and contact hazards.</p><p>Code 1950, §§ 45-82, 45-82.3; 1954, c. 191; 1966, c. 594, § 45.1-75; 1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0028" target="_blank" rel="noreferrer">28</a>, § 45.1-161.181; 1996, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0774" target="_blank" rel="noreferrer">774</a>; 1999, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0256" target="_blank" rel="noreferrer">256</a>; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0387" target="_blank" rel="noreferrer">387</a>.</p>',5),l=[n];function c(o,s,d,h,p,f){return a(),t("div",null,l)}const u=e(r,[["render",c]]);export{_ as __pageData,u as default};
