import{_ as e,o as i,c as t,O as a}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 45.2-812  Electrical equipment.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/45.2-812.md","filePath":"vacode/45.2-812.md"}'),l={name:"vacode/45.2-812.md"},n=a('<h1 id="§-45-2-812-electrical-equipment" tabindex="-1">§ 45.2-812 Electrical equipment. <a class="header-anchor" href="#§-45-2-812-electrical-equipment" aria-label="Permalink to &quot;§ 45.2-812  Electrical equipment.&quot;">​</a></h1><p>A. Electrical equipment that is taken into or used inby the last open crosscut or in other than an intake airway constitutes permissible equipment.</p><p>B. Permissible equipment that is used in an area specified in subsection A shall be maintained in permissible condition.</p><p>C. No electrical equipment shall be taken into or operated in any place where a methane level of one percent or more is detected.</p><p>D. Voltage limitations for underground installations of electrical equipment using direct or alternating current shall conform to the voltages provided in 30 C.F.R. § 18.47.</p><p>E. Electrical equipment shall be classified as permissible and shall be maintained in a permissible condition when such equipment is located within 150 feet of any pillar workings or longwall face.</p><p>F. Any electrical conductors and cables installed in or inby the last open crosscut, or within 150 feet of any pillar workings or longwall face, shall be:</p><ol><li><p>Shielded high-voltage cables supplying power to permissible longwall equipment or other equipment;</p></li><li><p>Interconnecting conductors and cables of permissible longwall equipment;</p></li><li><p>Conductors and cables of intrinsically safe circuits; or</p></li><li><p>Cables and conductors supplying power to low-voltage or medium-voltage permissible equipment.</p></li></ol><p>G. Electrical equipment shall be maintained in safe operating condition at all times while it is being used, and any unsafe condition shall be corrected promptly or the equipment shall be removed from service.</p><p>Code 1950, § 45-83; 1954, c. 191; 1966, c. 594, § 45.1-83; 1993, c. 442; 1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0028" target="_blank" rel="noreferrer">28</a>, § 45.1-161.193; 1999, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0256" target="_blank" rel="noreferrer">256</a>; 2011, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0826" target="_blank" rel="noreferrer">826</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0862" target="_blank" rel="noreferrer">862</a>; 2015, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0103" target="_blank" rel="noreferrer">103</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0397" target="_blank" rel="noreferrer">397</a>; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0387" target="_blank" rel="noreferrer">387</a>.</p>',10),r=[n];function o(s,c,p,d,g,h){return i(),t("div",null,r)}const f=e(l,[["render",o]]);export{m as __pageData,f as default};
